  constructor(public _api: ApiService,
                private _init:InitService,
                private _tokenCheck:TokenCheckService,
                public toastr: ToastsManager, vcr: ViewContainerRef,
                public sdConfig: NgbCarouselConfig) {

    this.currentUser = this._init.getUserInfo()
    this.showContents = this._init.checkCredential( this.mainCredential, true )

    this.toastr.setRootViewContainerRef(vcr);

    this._tokenCheck.getTokenStatus()
        .subscribe( res => {

          if( res.status ){
            this.showContents = this._init.checkCredential( this.mainCredential, true )
          }else{
            this.showContents = false
            jQuery("#loginModal").modal('show');
          }
        })

        sdConfig.interval = 20000;
        sdConfig.wrap = true;
  }

  ngOnInit() {
    this.getDeps()
    this.getData(1)
  }

  getDeps( ){
    this.dataDeps = this.fdepartamentos
  }

  getSups( dep ){
    this.dataSups = this.fSups
  }

  chgDep( event ){
    this.dep = event
    this.getMetas( event )
    this.getSups( event )
  }

  chgSup( event ){
    this.sup = event
    this.getData(event)
  }

  getMetas( dep ){
    this.dataMetas = this.fMetas
  }

  getData( sup ){

    let asesores = {}
    let keys = []

    for(let item of this.jsonData){
      if( asesores[item.nombre] ){
        asesores[item.nombre].push(item)
      }else{
        asesores[item.nombre] = [item]
        keys.push(item.nombre)
      }
    }

    this.numAsesores = keys.length
    this.dataAsesor = {}

    keys.sort()
    for(let k of keys){
      this.dataAsesor[k]=asesores[k]
    }

    this.timerFlag = true
    this.timeCount = 60

  }

  chgMode( type ){
    this.screen = !type
  }

  timerLoad( pause = false ){

    if( this.timerFlag ){
      if( this.timeCount == 0 ){

        this.getMetas( this.dep )
        this.getData(  this.sup )

      }else{
        if( this.timeCount > 0){
          this.timeCount--
          setTimeout( () => {
          this.timerLoad()
          }, 1000 )
        }
      }
    }else{
      if( pause ){
        setTimeout( () => {
        this.timerLoad( true )
        }, 1000 )
      }
    }
  }

